import "../registry/ENS.sol";
import "./IL2ReverseRegistrar.sol";
import "./SignatureReverseResolver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../resolvers/profiles/ITextResolver.sol";
import "../resolvers/profiles/INameResolver.sol";
import "../root/Controllable.sol";
import "../resolvers/Multicallable.sol";
import "../utils/LowLevelCallUtils.sol";

error NotOwnerOfContract();

/**
 * A L2 reverser registrar. Deployed to each L2 chain.
 * The contract will be verified on L1 Reverse Resolver under the namespace specified at constructor
 */
contract L2ReverseRegistrar is
    Multicallable,
    Ownable,
    IL2ReverseRegistrar,
    SignatureReverseResolver
{
    using ECDSA for bytes32;

    bytes32 public immutable L2ReverseNode;

    /*
     * @dev Constructor
     * @param _L2ReverseNode The namespace to set. The converntion is '${cointype}.reverse'
     * @param _coinType The cointype converted from the chainId of the chain this contract is deployed to.
     */
    constructor(
        bytes32 _L2ReverseNode,
        uint256 _coinType
    ) SignatureReverseResolver(_L2ReverseNode, _coinType) {
        L2ReverseNode = _L2ReverseNode;
    }

    modifier ownerAndAuthorisedWithSignature(
        bytes32 hash,
        address addr,
        address owner,
        uint256 inceptionDate,
        bytes memory signature
    ) {
        isOwnerAndAuthorisedWithSignature(
            hash,
            addr,
            owner,
            inceptionDate,
            signature
        );
        _;
    }

    function isAuthorised(address addr) internal view override returns (bool) {
        if (addr != msg.sender && !ownsContract(addr, msg.sender)) {
            revert Unauthorised();
        }
    }

    function isOwnerAndAuthorisedWithSignature(
        bytes32 hash,
        address addr,
        address owner,
        uint256 inceptionDate,
        bytes memory signature
    ) internal view returns (bool) {
        bytes32 message = keccak256(
            abi.encodePacked(hash, addr, owner, inceptionDate, coinType)
        ).toEthSignedMessageHash();
        bytes32 node = _getNamehash(addr);

        if (!ownsContract(addr, owner)) {
            revert NotOwnerOfContract();
        }

        if (
            !SignatureChecker.isValidERC1271SignatureNow(
                owner,
                message,
                signature
            )
        ) {
            revert InvalidSignature();
        }

        if (
            inceptionDate <= lastUpdated[node] || // must be newer than current record
            inceptionDate / 1000 >= block.timestamp // must be in the past
        ) {
            revert SignatureOutOfDate();
        }
    }

    /**
     * @dev Sets the name for a contract that is owned by a SCW using a signature
     * @param contractAddr The reverse node to set
     * @param owner The owner of the contract (via Ownable)
     * @param name The name of the reverse record
     * @param inceptionDate Date from when this signature is valid from
     * @param signature The signature of an address that will return true on isValidSignature for the owner
     * @return The ENS node hash of the reverse record.
     */
    function setNameForAddrWithSignatureAndOwnable(
        address contractAddr,
        address owner,
        string memory name,
        uint256 inceptionDate,
        bytes memory signature
    )
        public
        ownerAndAuthorisedWithSignature(
            keccak256(
                abi.encodePacked(
                    IL2ReverseRegistrar
                        .setNameForAddrWithSignatureAndOwnable
                        .selector,
                    name
                )
            ),
            contractAddr,
            owner,
            inceptionDate,
            signature
        )
        returns (bytes32)
    {
        bytes32 node = _getNamehash(contractAddr);
        _setName(node, name, inceptionDate);
        emit NameChanged(node, name);
        emit ReverseClaimed(contractAddr, node);
    }

    /**
     * @dev Sets the `name()` record for the reverse ENS record associated with
     * the calling account.
     * @param name The name to set for this address.
     * @return The ENS node hash of the reverse record.
     */
    function setName(string memory name) public override returns (bytes32) {
        return setNameForAddr(msg.sender, name);
    }

    /**
     * @dev Sets the `name()` record for the reverse ENS record associated with
     * the addr provided account.
     * Can be used if the addr is a contract that is owned by a SCW.
     * @param name The name to set for this address.
     * @return The ENS node hash of the reverse record.
     */

    function setNameForAddr(
        address addr,
        string memory name
    ) public authorised(addr) returns (bytes32) {
        bytes32 node = _getNamehash(addr);
        _setName(node, name, block.timestamp);
        emit ReverseClaimed(addr, node);
        return node;
    }

    /**
     * @dev Sets the name for a contract that is owned by a SCW using a signature
     * @param contractAddr The reverse node to set
     * @param owner The owner of the contract (via Ownable)
     * @param key The name of the reverse record
     * @param value The name of the reverse record
     * @param inceptionDate Date from when this signature is valid from
     * @param signature The signature of an address that will return true on isValidSignature for the owner
     * @return The ENS node hash of the reverse record.
     */
    function setTextForAddrWithSignatureAndOwnable(
        address contractAddr,
        address owner,
        string calldata key,
        string calldata value,
        uint256 inceptionDate,
        bytes memory signature
    )
        public
        ownerAndAuthorisedWithSignature(
            keccak256(
                abi.encodePacked(
                    IL2ReverseRegistrar
                        .setTextForAddrWithSignatureAndOwnable
                        .selector,
                    key,
                    value
                )
            ),
            contractAddr,
            owner,
            inceptionDate,
            signature
        )
        returns (bytes32)
    {
        bytes32 node = _getNamehash(contractAddr);
        _setText(node, key, value, inceptionDate);
    }

    /**
     * @dev Sets the `name()` record for the reverse ENS record associated with
     * the calling account.
     * @param key The key for this text record.
     * @param value The value to set for this text record.
     * @return The ENS node hash of the reverse record.
     */
    function setText(
        string calldata key,
        string calldata value
    ) public override returns (bytes32) {
        return setTextForAddr(msg.sender, key, value);
    }

    /**
     * @dev Sets the `text(key)` record for the reverse ENS record associated with
     * the addr provided account.
     * @param key The key for this text record.
     * @param value The value to set for this text record.
     * @return The ENS node hash of the reverse record.
     */

    function setTextForAddr(
        address addr,
        string calldata key,
        string calldata value
    ) public override authorised(addr) returns (bytes32) {
        bytes32 node = _getNamehash(addr);
        _setText(node, key, value, block.timestamp);
        return node;
    }

    /**
     * Returns the text data associated with an ENS node and key.
     * @param node The ENS node to query.
     * @param key The text data key to query.
     * @return The associated text data.
     */
    function text(
        bytes32 node,
        string calldata key
    ) external view virtual override returns (string memory) {
        return _text(node, key);
    }

    /**
     * Returns the name associated with an ENS node, for reverse records.
     * Defined in EIP181.
     * @param node The ENS node to query.
     * @return The associated name.
     */
    function name(
        bytes32 node
    ) external view virtual override returns (string memory) {
        return _name(node);
    }

    /**
     * Increments the record version associated with an ENS node.
     * May only be called by the owner of that node in the ENS registry.
     * @param addr The node to update.
     */
    function clearRecords(address addr) public virtual authorised(addr) {
        _clearRecords(addr);
    }

    function ownsContract(
        address contractAddr,
        address addr
    ) internal view returns (bool) {
        try Ownable(contractAddr).owner() returns (address owner) {
            return owner == addr;
        } catch {
            return false;
        }
    }

    function supportsInterface(
        bytes4 interfaceID
    )
        public
        view
        override(Multicallable, SignatureReverseResolver)
        returns (bool)
    {
        return
            interfaceID == type(IL2ReverseRegistrar).interfaceId ||
            super.supportsInterface(interfaceID);
    }
}
","keccak256":"0x68f6735c84966c0630b4b9bb1c3788541ee4ade55fd6e61ec5b57c5af0d35c11"},"contracts/reverseRegistrar/SignatureReverseResolver.sol":{"content":"pragma solidity >=0.8.4;

import "../registry/ENS.sol";
import "./ISignatureReverseResolver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../root/Controllable.sol";
import "../utils/LowLevelCallUtils.sol";

error InvalidSignature();
error SignatureOutOfDate();
error Unauthorised();

contract SignatureReverseResolver is Ownable, ISignatureReverseResolver {
    using ECDSA for bytes32;
    mapping(bytes32 => uint256) public lastUpdated;
    mapping(uint64 => mapping(bytes32 => mapping(string => string))) versionable_texts;
    mapping(uint64 => mapping(bytes32 => string)) versionable_names;
    mapping(bytes32 => uint64) internal recordVersions;

    bytes32 public immutable parentNode;
    uint256 public immutable coinType;

    /*
     * @dev Constructor
     * @param parentNode The namespace to set.
     * @param _coinType The cointype converted from the chainId of the chain this contract is deployed to.
     */
    constructor(bytes32 _parentNode, uint256 _coinType) {
        parentNode = _parentNode;
        coinType = _coinType;
    }

    modifier authorised(address addr) {
        isAuthorised(addr);
        _;
    }

    modifier authorisedSignature(
        bytes32 hash,
        address addr,
        uint256 inceptionDate,
        bytes memory signature
    ) {
        isAuthorisedWithSignature(hash, addr, inceptionDate, signature);
        _;
    }

    function getLastUpdated(
        bytes32 node
    ) internal view virtual returns (uint256) {
        return lastUpdated[node];
    }

    function isAuthorised(address addr) internal view virtual returns (bool) {}

    function isAuthorisedWithSignature(
        bytes32 hash,
        address addr,
        uint256 inceptionDate,
        bytes memory signature
    ) internal view returns (bool) {
        bytes32 message = keccak256(
            abi.encodePacked(hash, addr, inceptionDate, coinType)
        ).toEthSignedMessageHash();
        bytes32 node = _getNamehash(addr);

        if (!SignatureChecker.isValidSignatureNow(addr, message, signature)) {
            revert InvalidSignature();
        }

        if (
            inceptionDate <= lastUpdated[node] || // must be newer than current record
            inceptionDate / 1000 >= block.timestamp // must be in the past
        ) {
            revert SignatureOutOfDate();
        }
    }

    /**
     * @dev Sets the name for an addr using a signature that can be verified with ERC1271.
     * @param addr The reverse record to set
     * @param name The name of the reverse record
     * @param inceptionDate Date from when this signature is valid from
     * @param signature The resolver of the reverse node
     * @return The ENS node hash of the reverse record.
     */
    function setNameForAddrWithSignature(
        address addr,
        string memory name,
        uint256 inceptionDate,
        bytes memory signature
    )
        public
        authorisedSignature(
            keccak256(
                abi.encodePacked(
                    ISignatureReverseResolver
                        .setNameForAddrWithSignature
                        .selector,
                    name
                )
            ),
            addr,
            inceptionDate,
            signature
        )
        returns (bytes32)
    {
        bytes32 node = _getNamehash(addr);
        _setName(node, name, inceptionDate);
        emit ReverseClaimed(addr, node);
        return node;
    }

    /**
     * @dev Sets the name for an addr using a signature that can be verified with ERC1271.
     * @param addr The reverse record to set
     * @param key The key of the text record
     * @param value The value of the text record
     * @param inceptionDate Date from when this signature is valid from
     * @param signature The resolver of the reverse node
     * @return The ENS node hash of the reverse record.
     */
    function setTextForAddrWithSignature(
        address addr,
        string calldata key,
        string calldata value,
        uint256 inceptionDate,
        bytes memory signature
    )
        public
        authorisedSignature(
            keccak256(
                abi.encodePacked(
                    ISignatureReverseResolver
                        .setTextForAddrWithSignature
                        .selector,
                    key,
                    value
                )
            ),
            addr,
            inceptionDate,
            signature
        )
        returns (bytes32)
    {
        bytes32 node = _getNamehash(addr);
        _setText(node, key, value, inceptionDate);
        return node;
    }

    function _setText(
        bytes32 node,
        string calldata key,
        string calldata value,
        uint256 inceptionDate
    ) internal {
        versionable_texts[recordVersions[node]][node][key] = value;
        _setLastUpdated(node, inceptionDate);
        emit TextChanged(node, key, key, value);
    }

    /**
     * Returns the text data associated with an ENS node and key.
     * @param node The ENS node to query.
     * @param key The text data key to query.
     * @return The associated text data.
     */
    function _text(
        bytes32 node,
        string calldata key
    ) internal view returns (string memory) {
        return versionable_texts[recordVersions[node]][node][key];
    }

    function _setName(
        bytes32 node,
        string memory newName,
        uint256 inceptionDate
    ) internal virtual {
        versionable_names[recordVersions[node]][node] = newName;
        _setLastUpdated(node, inceptionDate);
        emit NameChanged(node, newName);
    }

    function _name(bytes32 node) internal view returns (string memory) {
        return versionable_names[recordVersions[node]][node];
    }

    /**
     * Increments the record version associated with an ENS node.
     * May only be called by the owner of that node in the ENS registry.
     * @param addr The node to update.
     */
    function _clearRecords(address addr) internal {
        bytes32 labelHash = LowLevelCallUtils.sha3HexAddress(addr);
        bytes32 reverseNode = keccak256(
            abi.encodePacked(parentNode, labelHash)
        );
        recordVersions[reverseNode]++;
        emit VersionChanged(reverseNode, recordVersions[reverseNode]);
    }

    /**
     * Increments the record version associated with an ENS node.
     * May only be called by the owner of that node in the ENS registry.
     * @param addr The node to update.
     * @param signature A signature proving ownership of the node.
     */
    function clearRecordsWithSignature(
        address addr,
        uint256 inceptionDate,
        bytes memory signature
    )
        public
        authorisedSignature(
            keccak256(
                abi.encodePacked(
                    ISignatureReverseResolver.clearRecordsWithSignature.selector
                )
            ),
            addr,
            inceptionDate,
            signature
        )
    {
        _clearRecords(addr);
    }

    /**
     * @dev Returns the node hash for a given account's reverse records.
     * @param addr The address to hash
     * @return The ENS node hash.
     */
    function node(address addr) public view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    parentNode,
                    LowLevelCallUtils.sha3HexAddress(addr)
                )
            );
    }

    function _getNamehash(address addr) internal view returns (bytes32) {
        bytes32 labelHash = LowLevelCallUtils.sha3HexAddress(addr);
        return keccak256(abi.encodePacked(parentNode, labelHash));
    }

    function _setLastUpdated(bytes32 node, uint256 inceptionDate) internal {
        lastUpdated[node] = inceptionDate;
    }

    function supportsInterface(
        bytes4 interfaceID
    ) public view virtual returns (bool) {
        return interfaceID == type(ISignatureReverseResolver).interfaceId;
    }
}
